@router.post("/login/", response_model=Token)
async def auth_login(
    user: UserLogin, db: AsyncDBSession = Depends(deps.get_db)
) -> Token:
    try:
        await logger.ainfo("Login attempt", username=user.username)
        db_user = (
            await db.scalars(select(User).where(User.username == user.username))
        ).first()
        if not db_user or not verify_password(user.password, db_user.password_hash):
            await logger.ainfo("Login failed", username=user.username)
            return JSONResponse(
                content={"success": False, "detail": "Invalid username or password"},
                status_code=401,
            )

        await logger.ainfo("Login successful", username=user.username)
        access_token_expires = timedelta(minutes=settings.ACCESS_TOKEN_EXPIRE_MINUTES)
        access_token = create_access_token(
            str(db_user.id), expires_delta=access_token_expires
        )

        return Token(access_token=access_token, token_type="bearer")
    except Exception as e:
        await logger.aexception("Exception during login", username=user.username)
        return JSONResponse(
            {
                "success": False,
                "detail": str(e) if settings.DEBUG else "Internal server error",
            },

@router.get("/latest")
async def feed_latest(
    before: datetime = datetime.now(tz=timezone.utc) + timedelta(days=1),
    after: datetime = datetime.fromtimestamp(0, tz=timezone.utc),
    user: UserDetail | None = Depends(deps.verify_jwt_to_uuid_or_none),
    db: AsyncDBSession = Depends(deps.get_db),
    aws: AWSSession = Depends(deps.get_aws_session),
    redis: Redis = Depends(deps.get_redis),
) -> Response:
    try:
        before = before.astimezone(timezone.utc).replace(tzinfo=None)
        after = after.astimezone(timezone.utc).replace(tzinfo=None)

        image_filters = [
            Image.created_at < before,
            Image.created_at > after,
            Image.public,
        ]

        if user is not None:
            image_filters[-1] = or_(Image.public, Image.owner_id == user.id)

        return JSONResponse(await build_feed(image_filters, db, aws, redis))
    except Exception as e:
        await logger.aexception("Error building latest feed", user=user.id)
        return JSONResponse(
            {
                "success": False,
                "detail": str(e) if settings.DEBUG else "Internal server error",
            },

@router.post("/upload/{privacy}/generate")
async def images_generate_upload_link(
    privacy: str,
    caption: str = "",
    user: UserDetail = Depends(deps.get_current_user),
    db: AsyncDBSession = Depends(deps.get_db),
    aws: AWSSession = Depends(deps.get_aws_session),
) -> Response:
    if privacy not in ["public", "private"]:
        return JSONResponse(
            content={
                "success": False,
                "detail": "privacy parameter should be 'public' or 'private'",
            },
            status_code=400,
        )

    public = privacy == "public"
    image_id = str(ULID().to_uuid4())
    db_image = Image(
        id=image_id,
        public=public,
        caption=caption,
        owner_id=user.id,
        content_type="image/jpeg",
    )

    presigned_post = create_presigned_post(aws, image_id)
    db_image.path = presigned_post["s3_uri"]
    create_response = presigned_post["create_response"] | {"id": image_id}

@router.get("/media/{image_id}")
async def images_retrieve(
    image_id: UUID4,
    user: UserDetail | None = Depends(deps.verify_jwt_to_uuid_or_none),
    db: AsyncDBSession = Depends(deps.get_db),
    aws: AWSSession = Depends(deps.get_aws_session),
    redis: Redis = Depends(deps.get_redis),
) -> Response:
    try:
        user_id = user.id if user is not None else None
        db_image = await get_image(db, image_id, user_id)
        if not db_image:
            return JSONResponse(
                {"success": False, "detail": "Image not found"}, status_code=404
            )

        return JSONResponse(
            {
                "success": True,
                "uri": await presigned_url_with_cache(
                    aws, redis, db_image.path, db_image.content_type
                ),
            }
        )

    except Exception as e:
        await logger.aexception("Error retrieving image", image=image_id, user=user_id)
        return JSONResponse({"success": False}, status_code=500)

@router.get("/{image_id}/likes")
async def images_get_likes(
    image_id: UUID4,
    user: UserDetail | None = Depends(deps.verify_jwt_to_uuid_or_none),
    db: AsyncDBSession = Depends(deps.get_db),
) -> Response:
    db_image = await get_image(db, image_id, user.id)
    if not db_image:
        return JSONResponse(
            {"success": False, "detail": "Image not found"}, status_code=404
        )

    liking_users = [
        {
            "id": str(like.user_id),
            "username": (await like.awaitable_attrs.user).username,
        }
        for like in await db_image.awaitable_attrs.likes
    ]

    return JSONResponse({"success": True, "likes": liking_users})

@router.post("/{image_id}/comment")
async def images_comment(
    image_id: UUID4,
    comment: CommentCreate,
    user: UserDetail = Depends(deps.get_current_user),
    db: AsyncDBSession = Depends(deps.get_db),
) -> Response:
    db_image = await get_image(db, image_id, user.id)
    if not db_image:
        return JSONResponse(
            {"success": False, "detail": "Image not found"}, status_code=404
        )

    (await db_image.awaitable_attrs.comments).append(
        Comment(
            id=ULID().to_uuid4(),
            user_id=user.id,
            image_id=image_id,
            content=comment.content,
        )
    )
    await db.commit()
    await db.refresh(db_image)

    return JSONResponse({"success": True})

@router.post("/{image_id}/comments/{comment_id}/update")
async def images_comment(
    image_id: UUID4,
    comment_id: UUID4,
    comment: CommentCreate,
    user: UserDetail = Depends(deps.get_current_user),
    db: AsyncDBSession = Depends(deps.get_db),
) -> Response:
    db_image = await get_image(db, image_id, user.id)
    if not db_image:
        return JSONResponse(
            {"success": False, "detail": "Image not found"}, status_code=404
        )

    for db_comment in await db_image.awaitable_attrs.comments:
        if db_comment.id == comment_id:
            db_comment.content = comment.content
            await db.commit()
            await db.refresh(db_image)
            break
    else:
        return JSONResponse(
            {"success": False, "detail": "Comment not found"}, status_code=404
        )

    return JSONResponse({"success": True})

@router.post("/{image_id}/comments/{comment_id}/delete")
async def images_comment(
    image_id: UUID4,
    comment_id: UUID4,
    user: UserDetail = Depends(deps.get_current_user),
    db: AsyncDBSession = Depends(deps.get_db),
) -> Response:
    db_image = await get_image(db, image_id, user.id)
    if not db_image:
        return JSONResponse(
            {"success": False, "detail": "Image not found"}, status_code=404
        )

    for comment in await db_image.awaitable_attrs.comments:
        if comment.id == comment_id:
            await db.delete(comment)
            await db.commit()
            await db.refresh(db_image)
            break
    else:
        return JSONResponse(
            {"success": False, "detail": "Comment not found"}, status_code=404
        )

    return JSONResponse({"success": True})

@router.post("/upload/{image_id}/confirm")
async def images_confirm_uploaded(
    image_id: UUID4,
    user: UserDetail = Depends(deps.get_current_user),
    db: AsyncDBSession = Depends(deps.get_db),
    aws: AWSSession = Depends(deps.get_aws_session),
) -> Response:
    db_image = (await db.scalars(select(Image).where(Image.id == image_id))).first()
    if (not db_image) or (db_image.owner_id != user.id):
        return JSONResponse(
            {"success": False, "detail": "Image not found"}, status_code=404
        )

    if db_image.uploaded:
        return JSONResponse(
            {"success": False, "detail": "Image upload already confirmed"},
            status_code=404,
        )

    if not verify_exists(aws, db_image.path):
        return JSONResponse(
            {"success": False, "detail": "Image with that ID doesn't exist in S3"},
            status_code=404,
        )

    db_image.uploaded = True
    db.add(db_image)
    await db.commit()
    await db.refresh(db_image)

@router.post("/{image_id}/like")
async def images_like(
    image_id: UUID4,
    user: UserDetail = Depends(deps.get_current_user),
    db: AsyncDBSession = Depends(deps.get_db),
) -> Response:
    db_image = await get_image(db, image_id, user.id)
    if not db_image:
        return JSONResponse(
            {"success": False, "detail": "Image not found"}, status_code=404
        )

    if (
        await db.scalars(
            select(Like).where(Like.user_id == user.id, Like.image_id == image_id)
        )
    ).first():
        return JSONResponse(
            {"success": False, "detail": "User has already liked this image"},
            status_code=400,
        )

    (await db_image.awaitable_attrs.likes).append(
        Like(user_id=user.id, image_id=image_id, image=db_image)
    )
    await db.commit()
    await db.refresh(db_image)

    return JSONResponse({"success": True})

